package test;

public class Count2withBreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count=0;
		int a=3;
		while(count++<3)
		{
			if(a<6)
			{
				a-=1;
				break;
			}

		System.out.println(a);
			}
		System.out.println(a);
}
	}

